B[1] = B[0] + (1/sqrt(3))*(rotate(90)*(A[0] - C[0]));
C[1] = C[0] + (1/sqrt(3))*(rotate(90)*(B[0] - A[0]));

draw(A[0]--B[0]--C[0]--cycle);
draw(A[1]--D);
draw(B[1]--E);
draw(C[1]--F);
