F = (0,-1/4);
P = (1,-1);
Q = (1,1/4);

real parab (real x) {
  return(-x^2);
}

draw(graph(parab,-1.5,1.5),red);
draw((-1.5,1/4)--(1.5,1/4),dashed);
draw(P--F);
draw(P--Q);

dot("$F$", F, SW);
dot("$P$", P, E);
dot("$Q$", Q, N);
