B = (-1,0);
C = (0,1);
D = (0,-1);
E = (1 + sqrt(3))/2*(1,1);
F = (1 + sqrt(3))/2*(-1,-1);
G = (1 + sqrt(3))/2*(1,-1);
H = (1 + sqrt(3))/2*(-1,1);

draw((-1.5,0)--(1.5,0));
